import queryString from 'query-string';
import CONFIG from '../configs';

const ajaxOpt = {
  method: 'GET',
  headers: {
    'X-RapidAPI-Host': 'star-wars-characters.p.rapidapi.com',
  },
};

export async function getUsers(options = {}) {
  // const defaultOptions = {
