import React, { Component } from 'react';
import { getCards } from '../../api/index';
// import styles from './Slider.module.css';

class Slider extends Component {
  constructor(props) {
    super(props);

    this.state = {
      heroes: [],
      isError: false,
      isLoading: false,
    };
  }

  load = async () => {
    this.setState({
      isLoading: true,
    });

    try {
      const { loadData } = this.props;
      const results = await getCards();
      this.setState({ heroes: results });
    } catch (error) {
      this.setState({ isError: true });
    } finally {
      this.setState({
        isLoading: false,
      });
    }
  };

  componentDidMount() {
    this.load();
  }

  render() {
    const { heroes, isError, isLoading } = this.state;

    if (isLoading) {
      return <h1>LOADING Heroes ...</h1>;
    }

    if (isError) {
      return <h1>ERROR HAPPENED</h1>;
    }

    const heroCards = heroes.map((hero, index) => (
      <article key={index}>
        <h2>{hero.characters}</h2>
      </article>
    ));

    return <div>{heroCards}</div>;
  }
}
export default Slider;
