import queryString from 'query-string';
import CONFIG from '../configs';

const ajaxOpt = {
  method: 'GET',
  headers: {
    'X-RapidAPI-Key': '22f544f51fmsh2c0e2e52c0ea668p19ee4djsn80402e515020',
    'X-RapidAPI-Host': 'star-wars-characters.p.rapidapi.com',
  },
};

export async function getUsers(options = {}) {
  const defaultOptions = {
    page: 1,
    results: CONFIG.DEFAULT_AMOUNT,
    format: 'json',
    seed: CONFIG.API_KEY,
    nat: 'gb',
    inc: CONFIG.DEFAULT_USER_DATA,
  };

  const finalOptions = {
    ...defaultOptions,
    ...options,
  };

  // const { page, results, nat, seed, inc } = finalOptions;

  const query = queryString.stringify(finalOptions, { arrayFormat: 'comma' });

  const res = await fetch(`${CONFIG.BASE_URL}?${query}`, ajaxOpt);

  const { results: users } = await res.json();

  return users;
}
