import React from 'react';
import styles from '../Card/Card.module.css';

const Card = (state) => {
  const { data: products, isLoading, isError } = state;
  console.log(products);

  if (!products) {
    return <p className={styles.empty}>No products available.</p>;
  }

  const productList = products.map((product) => (
    <article key={product.id} className={styles.card}>
      <h2>{product.title}</h2>
      <p>{product.description}</p>
      <img src={product.thumbnail} alt={product.title} className={styles.img} />
    </article>
  ));

  return (
    <>
      {isLoading && <h3 className={styles.loading}>Loading...</h3>}
      {isError && <h3 className={styles.error}>Error...</h3>}
      {products.length === 0 && !isLoading && !isError && (
        <p className={styles.empty}>No products available.</p>
      )}
      {productList}
    </>
  );
};

export default Card;
