import styles from '../Card/Card.module.css';

const Card = (state) => {
  const { data: product, isLoading, isError } = state;

  if (!product) {
    return <p className={styles.empty}>No products available.</p>;
  }

  // const productList = products.map((product) => (

  <article key={product.id} className={styles.card}>
    <div className={styles.content}>
      <h2 className={styles.title}>{product.title}</h2>
      <p className={styles.desc}>{product.description}</p>
    </div>
    <img src={product.thumbnail} alt={product.title} className={styles.img} />
  </article>;
  // );

  return (
    <>
      {isLoading && <h3 className={styles.loading}>Loading...</h3>}
