import React from 'react';
import styles from '../Cards/Cards.module.css';

const Cards = (state) => {
  const { products: cards, isLoading, isError } = state;
  console.log(cards);
  if (!cards) {
    return <p className={styles.empty}>No cards available.</p>;
  }

  const cardList = cards.map((card) => (
    <article key={card.id} className='card'>
      <h2>{card.title}</h2>
      <p>{card.description}</p>
      <img src={card.thumbnail} alt={card.title} />
    </article>
  ));
  return (
    <>
      {isLoading && <h3 className={styles.loading}>Loading...</h3>}
      {isError && <h3 className={styles.error}>Error...</h3>}
      {cards.length === 0 && !isLoading && !isError && (
        <p className={styles.empty}>No cards available.</p>
      )}
      {cardList}
    </>
  );
};

export default Cards;
