import React from 'react';
import './App.css';
import DataLoader from './components/DataLoader';
import Slider from './components/Slider';
// import Cards from './components/Cards';
import { getCards } from './api';

const Cards = ({ products: cards, isLoading, isError }) => {
  console.log(cards, isLoading, isError);
  if (Array.isArray(cards)) {
    alert('array');
  } else {
    alert('empty'); // or handle the case when cards is not an array
  }

  const cardList = cards.map((card) => (
    <article key={card.id} className='card'>
      <h2>{card.title}</h2>
      <p>{card.description}</p>
      <img src={card.thumbnail} alt={card.title} />
    </article>
  ));
  return (
    <>
      {isLoading && <h3 className={styles.loading}>Loading...</h3>}
      {isError && <h3 className={styles.error}>Error...</h3>}
      {cards.length === 0 && !isLoading && !isError && (
        <p className={styles.empty}>No cards available.</p>
      )}
      {cardList}
    </>
  );
};

function App(props) {
  return (
    <DataLoader
      loadData={getCards}
      render={(cards) => (
        <Slider>
          <Cards cardList={cards} />
        </Slider>
      )}
    />
  );
}
export default App;
