import React, { Component } from 'react';
import { getCards } from '../../api/index';
import styles from './Slider.module.css';

class Slider extends Component {
  constructor(props) {
    super(props);

    this.state = {
      heroes: [],
      isError: false,
      isLoading: false,
    };
  }

  load = async () => {
    try {
      const result = await getCards();
      this.setState({ heroes: result });
      console.log(this.state);
    } catch (error) {
      this.setState({ isError: true });
    } finally {
      this.setState({
        isLoading: false,
      });
    }
  };

  componentDidMount() {
    this.load();
  }

  render() {
    const { heroes, isError, isLoading } = this.state;

    if (isLoading) {
      return <h1>LOADING Heroes ...</h1>;
    }

    if (isError) {
      return <h1>ERROR HAPPENED</h1>;
    }

    const heroCards = heroes.map(({ name, image }) => (
      <article>
        <h2>{name}</h2>
        <img className={styles.pic} src={image} alt={name} />
      </article>
    ));

    return <div>{heroCards}</div>;
  }
}
export default Slider;
